import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import "./userManage.scss";
import {getAllUsers} from "../../services/userService"
import ModalUser from './ModalUser'
class UserManage extends Component {

    constructor(props){
        super(props);
        this.state ={
            arrUsers: [],
            isOpenModalUser: false,
        }
    }

    async componentDidMount() {
        let response = await getAllUsers('ALL');
        console.log('get user from node.js:', response)
        if(response && response.errCode===0){
            this.setState({
                arrUsers: response.users
            })
        }
    }

    handleAddNewUser = () =>{
        this.setState({
            isOpenModalUser:true,
        })
    }
    /** Life CycleCycle
     * Run component:
     * 1.run constructor -> init state
     * 2. Did mount(set state): born unmount
     * 3. Render(re-render)
     * 
     * 
     * 
     * 
     * 
     */

    render() {
        console.log('check render', this.state)
        let arrUsers = this.state.arrUsers;
        //properties; nested
        return (
            <div className="user-container">
                <ModalUser
                    isOpen = {this.state.isOpenModalUser}
                    test ={'abc'}
                />
                <div className='title text-center'>Manage user with Nghia</div>
                <div className='mx-1'>
                    <button className='btn btn-primary px-3'
                    onClick={() => this.handleAddNewUser()}><i class="fas fa-plus"></i>Add new user</button>
                </div>
                <div className='users-table mt-3 mx-1'>
                    <table id="customers">
                        <tr>
                            <th>Email</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Adress</th>
                            <th>Actiones</th>
                        </tr>
                        
                            {
                                arrUsers && arrUsers.map((item,index) =>{                                    return (
                                        <tr>
                                            <td>{item.email}</td>
                                            <td>{item.firstName}</td>
                                            <td>{item.lastName}</td>
                                            <td>{item.address}</td>
                                            <td>
                                                <button className="btn-edit"><i  class="fas fa-pencil-alt"></i></button>
                                                <button className="btn-delete"><i  class="fas fa-trash-alt"></i></button>
                                            </td>
                                        </tr>
                                    )
                                })
                            }
                            
                      
                    </table>
                </div>
            </div>
        );
    }

}

const mapStateToProps = state => {
    return {
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserManage);
