import axios from 'axios';
import _ from 'lodash';
import config from './config';

const instance = axios.create({
    baseURL: process.env.REACT_APP_BACKEND_URL,
    withCredentials: true
});

const createError = (httpStatusCode, statusCode = null, errorMessage = 'An error occurred', problems = null, errorCode = '') => {
    // Khởi tạo Error với thông báo lỗi cụ thể
    const error = new Error(errorMessage);
    error.httpStatusCode = httpStatusCode;
    error.statusCode = statusCode;
    error.problems = problems;
    error.errorCode = errorCode.toString();
    return error;
};

export const isSuccessStatusCode = (s) => {
    // Có thể là chuỗi hoặc số
    const statusType = typeof s;
    return (statusType === 'number' && s === 0) || (statusType === 'string' && s.toUpperCase() === 'OK');
};

instance.interceptors.response.use(
    (response) => {
        const { data } = response;
        // Kiểm tra mã trạng thái và thông điệp lỗi
        if (data.hasOwnProperty('s') && !isSuccessStatusCode(data['s']) && data.hasOwnProperty('errmsg')) {
            return Promise.reject(createError(response.status, data['s'], data['errmsg'], null, data['errcode'] || ""));
        }

        // Trả về dữ liệu trực tiếp cho callback
        if (data.hasOwnProperty('s') && data.hasOwnProperty('d')) {
            return data['d'];
        }

        // Xử lý trường hợp đặc biệt
        if (data.hasOwnProperty('s') && _.keys(data).length === 1) {
            return null;
        }
        
        return response.data;
    },
    (error) => {
        const { response } = error;
        // Nếu không có phản hồi, từ chối với lỗi gốc
        if (!response) {
            return Promise.reject(new Error('Network Error: Unable to connect.'));
        }

        const { data } = response;

        // Xử lý thông báo lỗi cụ thể
        if (data.hasOwnProperty('s') && data.hasOwnProperty('errmsg')) {
            return Promise.reject(createError(response.status, data['s'], data['errmsg']));
        }

        // Kiểm tra các trường dữ liệu khác để tạo lỗi
        if (data.hasOwnProperty('code') && data.hasOwnProperty('message')) {
            return Promise.reject(createError(response.status, data['code'], data['message'], data['problems'] || null));
        }

        // Cung cấp giá trị mặc định cho các tham số
        return Promise.reject(createError(response.status, null, 'An unexpected error occurred'));
    }
);

export default instance;
