import React, { Component } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import "./userManage.scss";


class ModalUser extends Component {

   constructor(props){
      super(props);
      this.state ={
         email: '',
         password: '',
         firstName: '',
         lastName: '',
         address: ''
      }
    }

    componentDidMount() {
    }

    toggle =() =>{
      this.props.toggleFromParent();
    }

    handleOnChageInput = (event,id) =>{
      //bad code
      this.state[id] = event.target.value;
      this.setState({
         ...this.state
      }, () =>{
         console.log('check bad state:', this.state)
      })

      //good code
      console.log('event 1',event.target.value,id)
    }
   
    render() {
      console.log('check child props', this.props);
      console.log('check child open modal', this.props.isOpen);
        return (
            <Modal 
               isOpen={this.props.isOpen} 
               toggle={() => {this.toggle()}} 
               className='modal-user-container'
               size ="lg"
            >
               <ModalHeader toggle={() => {this.toggle()}}>Create a New User</ModalHeader>
               <ModalBody>
               <div className='modal-user-body'>
                  <div className='input-container'>
                     <label>Email: </label>
                     <input type="text" onChange={(event) => {this.handleOnChageInput(event,"email")}}/>
                  </div>
                  <div className='input-container'>
                     <label>Password: </label>
                     <input type="password"  onChange={(event) => {this.handleOnChageInput(event,"password")}}/>
                  </div>
                  <div className='input-container'>
                     <label>First Name: </label>
                     <input type="text"onChange={(event) => {this.handleOnChageInput(event,"firstName")}}/>
                  </div> 
                  <div className='input-container'>
                     <label>Last Name: </label>
                     <input type="text"onChange={(event) => {this.handleOnChageInput(event,"lastName")}}/>
                  </div>
                  <div className='input-container max-width-input'>
                     <label>Adress: </label>
                     <input type="text"onChange={(event) => {this.handleOnChageInput(event,"address")}}/>
                  </div>    
               </div>
               
                        
               </ModalBody>
               <ModalFooter>
                  <Button color="primary" className="px-3" onClick={() => {this.toggle()}}>Save</Button>{' '}
                  <Button color="secondary" className="px-3" onClick={() => {this.toggle()}}>Cancel</Button>
               </ModalFooter>
            </Modal>
        )
    }

}

const mapStateToProps = state => {
    return {
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ModalUser);
