import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGooglePlusG, faFacebook } from '@fortawesome/free-brands-svg-icons';
import * as actions from "../../store/actions";

import './Login.scss';
import { FormattedMessage } from 'react-intl';


class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: 'nghiale',
            password: '123',
        }
    }

    hand
    render() {
        //JSX
        return (
            <div className='login_background'>
                <div className='login_container'>
                    <div className='login_content row'>
                        <div className='col-12 text-login'>Login</div>
                        <div className='col-12 login-input'>
                            <label>User Name:</label>
                            <input type='text' 
                            className='form-control' 
                            placeholder='Enter your User Name'
                            value={this.state.username}
                            onChange={(event) => this.handleOnChangeInput(event)}></input>
                        </div>
                        <div className='col-12 login-input'>
                            <label>Password:</label>
                            <input type='password' className='form-control' placeholder='Enter your User Pasword'></input>
                        </div>
                        <div className='col-12'>
                            <button className='btn-login'>LogIn</button>
                        </div>
                        <div className='col-12'>
                            <span className='forgot-password'>Forgot your password</span>
                        </div>
                        <div className='col-12 text-center mt-3'>
                            <span className='forgot-password'>Or Login with:</span>
                        </div>
                        <div className='col-12 social-login'>
                            <FontAwesomeIcon className="google" icon={faGooglePlusG} />
                            <FontAwesomeIcon className='facebook' icon={faFacebook} />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        lang: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) => dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
